import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [peliculas, setPeliculas] = useState([]);
    const [peliculasFiltradas, setPeliculasFiltradas] = useState([]);
    const [busqueda, setBusqueda] = useState('');
    const [modoDescripcion, setModoDescripcion] = useState(false);
    const [recomendacion, setRecomendacion] = useState('');
    const [mostrarFormulario, setMostrarFormulario] = useState(false);
    const [peliculaEditando, setPeliculaEditando] = useState(null);

    // Cargar películas desde el backend
    useEffect(() => {
        fetch('http://localhost:4000/api/peliculas')
            .then(res => res.json())
            .then(data => {
                setPeliculas(data);
                setPeliculasFiltradas(data);
            })
            .catch(err => console.error('Error al obtener películas:', err));
    }, []);

    // Búsqueda tradicional
    const handleBuscar = (e) => {
        e.preventDefault();
        const texto = busqueda.toLowerCase();
        const resultado = peliculas.filter(p =>
            p.titulo.toLowerCase().includes(texto) ||
            p.genero.toLowerCase().includes(texto)
        );
        setPeliculasFiltradas(resultado);
        setRecomendacion('');
    };

    // Búsqueda por IA
    const handleBuscarPorDescripcion = async () => {
        try {
            const res = await fetch('http://localhost:4000/api/recomendaciones', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ 
                    prompt: `Dame una recomendación basada en esta descripción: ${busqueda}. 
                            Usa solo películas de este catálogo: ${peliculas.map(p => p.titulo).join(', ')}` 
                })
            });
            const data = await res.json();
            setRecomendacion(data.recomendacion);
            const seleccionadas = peliculas.filter(p =>
                data.recomendacion.toLowerCase().includes(p.titulo.toLowerCase())
            );
            if (seleccionadas.length > 0) {
                setPeliculasFiltradas(seleccionadas);
            }
        } catch (err) {
            console.error('Error con IA:', err);
        }
    };

    // Manejar edición
    const handleEditar = (pelicula) => {
        setPeliculaEditando(pelicula);
        setMostrarFormulario(true);
    };

    return (
        <div className="App">
            <h1>CecyFlix</h1>

            {/* Formulario de búsqueda */}
            <form className="buscador" onSubmit={handleBuscar}>
                <input
                    type="text"
                    placeholder={modoDescripcion ? 'Describe la peli que buscas...' : 'Busca por título o género'}
                    value={busqueda}
                    onChange={e => setBusqueda(e.target.value)}
                />
                <button type="submit">
                    {modoDescripcion ? 'Buscar con IA' : 'Buscar'}
                </button>
                <button type="button" onClick={() => setModoDescripcion(!modoDescripcion)}>
                    {modoDescripcion ? 'Modo normal' : 'Modo IA'}
                </button>
            </form>

            {/* Botón para agregar nueva película */}
            <button onClick={() => {
                setPeliculaEditando(null);
                setMostrarFormulario(true);
            }}>
                Agregar Película
            </button>

            {/* Formulario para agregar/editar */}
            {mostrarFormulario && (
                <FormularioAgregar
                    pelicula={peliculaEditando}
                    onClose={() => setMostrarFormulario(false)}
                    onRefresh={() => {
                        fetch('http://localhost:4000/api/peliculas')
                            .then(res => res.json())
                            .then(data => {
                                setPeliculas(data);
                                setPeliculasFiltradas(data);
                            });
                    }}
                />
            )}

            {/* Recomendación IA */}
            {recomendacion && (
                <div className="bloque-recomendaciones">
                    <h2>IA sugiere:</h2>
                    <p>{recomendacion}</p>
                </div>
            )}

            {/* Galería de películas */}
            <div className="grid">
                {peliculasFiltradas.map((p, i) => (
                    <div className="tarjeta" key={i} onClick={() => handleEditar(p)}>
                        <img src={p.poster} alt={p.titulo} />
                        <div className="info">
                            <h3>{p.titulo}</h3>
                            <p>{p.genero}</p>
                            <span>{p.descripcion?.slice(0, 60)}...</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
